import React from "react";
import axios from "axios";

export default function SignIn() {
  async function handleSignIn() {
    try {
      const res = await axios.get("http://localhost:8080/api/auth/google");
      if (res.data && res.data.url) window.location.href = res.data.url;
      else alert("Could not get auth URL");
    } catch (e) {
      alert("Error contacting backend: " + e.message);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 p-6">
      <div className="bg-white/20 backdrop-blur-xl shadow-2xl rounded-3xl w-full max-w-md p-10 border border-white/30 animate-fadeIn">

        {/* Logo / Icon */}
        <div className="flex justify-center mb-6">
          <div className="w-20 h-20 bg-white/20 rounded-full flex items-center justify-center shadow-md">
            <svg
              className="w-10 h-10 text-white"
              fill="currentColor"
              viewBox="0 0 24 24"
            >
              <path d="M12 3a9 9 0 109 9 9.01 9.01 0 00-9-9zm-.25 4h1.5v6h-1.5zm.75 10.25a1.25 1.25 0 111.25-1.25 1.26 1.26 0 01-1.25 1.25z" />
            </svg>
          </div>
        </div>

        {/* Heading */}
        <h2 className="text-center text-3xl font-bold text-white drop-shadow-lg">
          Sign in with Google
        </h2>
        <p className="text-center text-white/90 mt-2 text-sm">
          You’ll be redirected to Google to grant Calendar permissions.
        </p>

        {/* Button */}
        <button
          onClick={handleSignIn}
          className="mt-8 w-full py-3 bg-white text-indigo-600 font-semibold rounded-xl shadow-lg hover:bg-indigo-100 transition-all duration-300 transform hover:scale-[1.03]"
        >
          Continue with Google
        </button>

        {/* Info Card */}
        <div className="mt-6 text-center text-white/90 text-sm bg-white/10 p-4 rounded-xl border border-white/20">
          After approval, you’ll come back here to link a phone number to your
          Google Calendar.
        </div>
      </div>
    </div>
  );
}
