import React, {useState, useEffect} from 'react';
import axios from 'axios';
export default function Connect(){
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  useEffect(()=>{
    const params = new URLSearchParams(window.location.search);
    const e = params.get('email');
    if(e) setEmail(e);
  },[]);

  async function handleLink(e){
    e.preventDefault();
    try{
      const res = await axios.post('http://localhost:8080/api/connect-phone', { email, phoneNumber: phone });
      alert('Linked: ' + JSON.stringify(res.data));
    }catch(err){
      alert('Error: '+err.response?.data?.error || err.message);
    }
  }

  return (
    <div className="container">
      <h2>Connect Phone Number</h2>
      <p className="small">Enter phone number to link it to the Google Calendar you just authorized.</p>
      <form onSubmit={handleLink}>
        <label>Email</label><br/>
        <input className="input" value={email} onChange={e=>setEmail(e.target.value)} required />
        <br/><br/>
        <label>Phone number (E.164 preferred)</label><br/>
        <input className="input" value={phone} onChange={e=>setPhone(e.target.value)} required />
        <br/><br/>
        <button className="btn" type="submit">Connect Phone</button>
      </form>
      <div className="card small">Once connected, use the Call Mira page to trigger events using this phone number.</div>
    </div>
  );
}
