import React from "react";
import axios from "axios";

export default function SignIn() {
  async function handleSignIn() {
    try {
      const res = await axios.get("http://localhost:8080/api/auth/google");
      if (res.data && res.data.url) window.location.href = res.data.url;
      else alert("Could not get auth URL from backend.");
    } catch (e) {
      alert("Error contacting backend: " + e.message);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 px-4 py-6">

      <div className="w-full max-w-md backdrop-blur-xl bg-white/20 shadow-2xl rounded-3xl p-10 border border-white/30 animate-fadeIn transform transition-all duration-300 hover:scale-[1.02]">

        {/* Page Title */}
        <h2 className="text-4xl font-extrabold text-center text-white drop-shadow-xl">
          Welcome to Mira
        </h2>

        <p className="text-center text-white/80 text-sm mt-2">
          Connect your Google Calendar to begin.
        </p>

        {/* Google Button */}
        <button
          onClick={handleSignIn}
          className="mt-8 w-full flex items-center justify-center gap-3 bg-white text-gray-900 py-3 rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-300 font-semibold hover:bg-gray-100 transform hover:scale-[1.03]"
        >
          {/* Google Icon */}
          <img
            src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg"
            alt="Google Logo"
            className="w-6 h-6"
          />
          Continue with Google
        </button>

        {/* Information Card */}
        <div className="mt-8 bg-white/10 backdrop-blur-lg rounded-2xl border border-white/20 p-5 shadow-inner text-white/90">
          <p className="text-base font-semibold">Next Step</p>
          <p className="text-sm mt-1 leading-relaxed">
            After granting permission, you will return here to link your phone
            number with your Google Calendar so Mira can manage event calls.
          </p>
        </div>

      </div>
    </div>
  );
}
