import React from 'react';
import axios from 'axios';

export default function SignIn(){
  async function handleSignIn(){
    try{
      // The frontend still makes a request to the backend to initiate the OAuth flow.
      // The backend should respond with the Google auth URL.
      const res = await axios.get('http://localhost:8080/api/auth/google');

      if(res.data && res.data.url) {
        // Redirect the user to the Google consent screen
        window.location.href = res.data.url;
      } else {
        alert('Could not get auth URL from backend.');
      }
    }catch(e){
      alert('Error contacting backend: ' + e.message);
    }
  }

  return (
    // Outer container: Centers the content both horizontally and vertically (min-h-screen, flex)
    // and applies a soft background color.
    <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">

      {/* Content Card: A central, visually distinct card for the sign-in form. */}
      <div className="w-full max-w-sm p-6 bg-white rounded-xl shadow-2xl space-y-6 transform hover:shadow-3xl transition duration-300">

        {/* Title */}
        <h2 className="text-3xl font-extrabold text-gray-900 text-center">
          Sign In with Google
        </h2>

        {/* Description */}
        <p className="text-sm text-gray-500 text-center">
          <span className="font-medium text-indigo-600">Note:</span> This process will open Google's consent screen to request calendar access for seamless integration.
        </p>

        {/* Sign-in Button */}
        <button 
          className="w-full flex items-center justify-center px-4 py-3 border border-transparent text-base font-medium rounded-lg shadow-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out transform hover:scale-[1.01]"
          onClick={handleSignIn}
        >
          {/* Using a simple icon for visual appeal */}
          <svg className="w-5 h-5 mr-3" viewBox="0 0 24 24" fill="currentColor">
            <path d="M12.0003 4.90909C14.4996 4.90909 16.5262 5.92212 17.8488 7.34868L20.5901 4.60734C18.6713 2.76632 16.0354 1.81818 12.0003 1.81818C7.5794 1.81818 3.71539 3.51139 1.94299 6.27582L5.80804 9.18349C6.41731 7.23098 8.01633 6.00282 10.0334 6.00282L10.0334 9.48971C8.75669 9.48971 7.42066 9.20818 6.55022 8.35411L2.68519 11.2618C3.89675 14.152 7.66986 16.0909 12.0003 16.0909C13.2045 16.0909 14.3639 15.9525 15.4285 15.6575L19.2936 18.5652C17.5212 21.3296 13.6572 23.0227 12.0003 23.0227C8.13524 23.0227 4.54929 21.4646 1.94299 18.9995L5.80804 16.0918C6.39864 16.7118 7.07094 17.2608 7.80933 17.7289L12.0003 17.7289C13.2842 17.7289 14.4996 17.4764 15.5898 17.0278C16.6801 16.5792 17.6534 15.9392 18.4735 15.1328L22.3386 12.2251C20.6152 9.61066 17.8488 7.72727 15.0601 7.72727L12.0003 7.72727V4.90909Z" fill="#FFF"/>
          </svg>
          Sign in with Google
        </button>

        {/* Information Box */}
        <div className="p-4 text-sm text-center text-indigo-700 bg-indigo-100 rounded-lg border border-indigo-200">
          <p className="font-medium">Next Step</p>
          <p className="mt-1">
            After successfully granting consent, you'll be redirected back to link a phone number to your calendar integration.
          </p>
        </div>
      </div>
    </div>
  );
}
